</template>
<script type="text/javascript">
export default {
    data() {
        return {
            info: {},
            items: [],
            adList: [],
            newsList: [],
            departmentList: [],
            personList: [],

        }
    },
    methods: {
        goInArticle(id) {
            debugger
            this.$router.push({
                path: '/index/articlePage',
                query: {
                    id: id,
                    title: this.info.jc,
                }
            })
        },
        goMoreNews() {
            this.$router.push({
                path: '/index/centerActiveList',
                query: {
                    jgid: this.info.id
                }
            })
        },
        toOnlinePay() {
            let state = this.$store.getters.loginState;
            if (state) {
                this.$router.push({ path: '/index/onlinePay', query: { jgid: this.info.id } });
            } else {
                this.$router.push({ path: '/index/login' });
            }
        },
        loadPerson(id, name) {
            this.$router.push({
                path: '/index/introArticle',
                query: {
                    xgjgid: id,
                    lx: '3',
                    title: name + '简介',
                }
            })
        },
        loadDepartment(id, name) {
            this.$router.push({
                path: '/index/introArticle',
                query: {
                    xgjgid: id,
                    lx: '2',
                    title: name + '简介',
                }
            })
        },
        loadIntro() {
            this.$router.push({
                path: '/index/introArticle',
                query: {
                    xgjgid: this.info.id,
                    lx: '1',
                    title: '机构介绍',
                }
            })
        },
        loadNotice() {
            let params = {
                xgjgid: this.info.id,
                wzlx: '02'
            }
            this.api.getArticleList(params).then(
                res => {
                    console.log(res);
                    this.$router.push({
                        path: '/index/treatmentNoticeList',
                        query: {
                            jgid: this.info.id
                        }
                    })
                }, err => {
                    console.log(err);
                })
        },
        goAppointment() {
            this.$router.push({
                path: '/index/appointment',
                query: { jgid: this.info.id }
            })
        },
        loadAdList() {
            let params = {
                xgjgid: this.info.id,
                wzlx: '03',
                length: '5',
            }
            this.api.getArticleList(params).then(
                res => {

                    console.log(res);
                    this.adList = res.data;
                }, err => {
                    console.log(err);
                })
        },
        loadNewsList() {
            let params = {
                xgjgid: this.info.id,
                wzlx: "01",
                length: "3",
            }
            this.api.getArticleList(params).then(
                res => {

                    console.log(res);
                    this.newsList = res.data;
                }, err => {
                    console.log(err);
                })
        },
        loadDepartmentList() {
            let params = {
                jgid: this.info.id,
            }
            this.api.getDepartmentList(params)
                .then(
                    res => {
                        this.departmentList = res.data;
                    }
                )
        },
        loadPersonList() {
            let params = {
                jgid: this.info.id,
            }
            this.api.getPersonList(params)
                .then(
                    res => {
                        this.personList = res.data;
                    }
                )
        },
        loadHisInfo() {
            let params = {
                jgid: this.info.id,
            }
            let _this = this;
            this.api.getHisInfo(params)
                .then(
                    res => {
                        debugger
                        res.data[0].id = _this.info.id;
                        this.$set(this.$data, 'info', res.data[0]);
                        this.$store.commit('setCurrentHis', this.info);
                        this.$store.commit('setPageTitle', this.info.jc);
                    }
                )
        },
    },
    components: {},
    mounted() {
        this.info = this.$route.query;
        debugger
        this.loadAdList();
        this.loadNewsList();
        this.loadDepartmentList();
        this.loadPersonList();
        this.loadHisInfo();
    },
    computed: {
        handleUser() {
            return this.$store.getters.getHandleUser;
        },
    }
}

</script>
