<script type="text/javascript">
export default {
    data() {
        return {
            info: {},
            items: [],
            adList: [],
            newsList: [],
            departmentList: [],
            personList: [],

        }
    },
    methods: {
        toArchives() {
            this.$router.push({
                path: '/index/archives' 
            })
        },
        toOthers() {
            this.$messagebox('此功能还未开放');
        },
        goMedical() {
            this.$router.push({
                path: '/index/medicalRecord' 
            })
        },
        goInArticle(id) {
            
            this.$router.push({
                path: '/index/articlePage',
                query: {
                    id: id,
                    title: this.info.jc,
                }
            })
        },
        goMoreNews() {
            this.$router.push({
                path: '/index/centerActiveList',
                query: {
                    jgid: this.info.id
                }
            })
        },
        toOnlinePay() {
            this.handle = this.$store.getters.getHandleUser;
            if (this.handle.lxdh != "15828051331") {
                this.$messagebox('功能近期即将开放');
                return;
            }
           
            let state = this.$store.getters.loginState;
            if (state) {
                this.$router.push({ path: '/index/onlinePay', query: { jgid: this.info.id } });
            } else {
                this.$router.push({ path: '/index/login' });
            }
        },
        loadPerson(id, name) {
            this.$router.push({
                path: '/index/introArticle',
                query: {
                    xgjgid: id,
                    lx: '3',
                    title: name + '简介',
                }
            })
        },
        loadDepartment(id, name) {
            this.$router.push({
                path: '/index/introArticle',
                query: {
                    xgjgid: id,
                    lx: '2',
                    title: name + '简介',
                }
            })
        },
        loadIntro() {
            this.$router.push({
                path: '/index/introArticle',
                query: {
                    xgjgid: this.info.id,
                    lx: '1',
                    title: '机构介绍',
                }
            })
        },
        loadNotice() {
            let params = {
                xgjgid: this.info.id,
                wzlx: '02'
            }
            this.api.getArticleList(params).then(
                res => {
                    console.log(res);
                    this.$router.push({
                        path: '/index/treatmentNoticeList',
                        query: {
                            jgid: this.info.id
                        }
                    })
                }, err => {
                    console.log(err);
                })
        },
        goAppointment() {
            this.handle = this.$store.getters.getHandleUser;
            console.log(this.handle)
            if (this.handle.lxdh != "15828051331") {
                this.$messagebox('功能近期即将开放');
                return;
            }

            this.$router.push({
                path: '/index/appointment',
                query: { jgid: this.info.id }
            })
        },
        loadAdList() {
            let params = {
                xgjgid: this.info.id,
                wzlx: '03',
                length: '5',
            }
            this.api.getArticleList(params).then(
                res => {

                    console.log(res);
                    this.adList = res.data;
                }, err => {
                    console.log(err);
                })
        },
        loadNewsList() {
            let params = {
                xgjgid: this.info.id,
                wzlx: "01",
                length: "3",
            }
            this.api.getArticleList(params).then(
                res => {
                    
                    console.log(res);
                    this.newsList = res.data;
                }, err => {
                    console.log(err);
                })
        },
        loadDepartmentList() {
            let params = {
                jgid: this.info.id,
            }
            this.api.getDepartmentList(params)
                .then(
                    res => {
                        this.departmentList = res.data;
                    }
                )
        },
        loadPersonList() {
            let params = {
                jgid: this.info.id,
            }
            this.api.getPersonList(params)
                .then(
                    res => {
                        this.personList = res.data;
                    }
                )
        },
        loadHisInfo() {
            
            let params = {
                jgid: this.info.id,
            }
            let _this = this;
            this.api.getHisInfo(params)
                .then(
                    res => {
                        
                        res.data[0].id = this.info.id;
                        this.$set(this.$data, 'info', res.data[0]);
                        this.$store.commit('setCurrentHis', this.info);
                        this.$store.commit('setPageTitle', this.info.jc);
                    }
                )
        },
    },
    components: {},
    mounted() {
        if(this.$route.query.dyms==1){
            this.$store.commit('setEnterMode',1);
            this.$store.commit('setBackBtn',false);
        }else{
            this.$store.commit('setEnterMode',0);
        }
        this.info = this.$route.query;
        
        this.loadAdList();
        this.loadNewsList();
        this.loadDepartmentList();
        this.loadPersonList();
        this.loadHisInfo();
    },
    computed: {
        handleUser() {
            return this.$store.getters.getHandleUser;
        },
    }
}

</script>
